const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
export default function validation(userdata, errors, setErrors) {
  if (!userdata.username) {
    setErrors({ ...errors, username: "Complete éste campo" });
  }
  if (!regexEmail.test(userdata.username)) {
    errors.username = "El usuario debe ser un email";
  }
  if (userdata.username.length() > 35) {
    errors.username = "El usuario no debe superar los 35 caracteres";
  }
  if (!userdata.password.match(/\d/)) {
    errors.password = "La contraseña debe contener al menos un numero";
  }
  if (userdata.password.length < 6 || userdata.password.length > 10) {
    errors.password = "La contraseña debe tener entre 6 y 10 carácteres";
  }
  return errors;
}
